import React, { Suspense } from 'react';
import IconPerson from './Icon/IconPerson';
import { fetchArtistJSON } from '../api';
import { createResource } from 'react-cache';
import Img from './Img';

const ArtistResource = createResource(fetchArtistJSON);
class ArtistDetails extends React.Component {
  render() {
    const artist = ArtistResource.read(this.props.id);
    return <ArtistHeader artist={artist} />;
  }
}

function ArtistHeader({ artist }) {
  return (
    <div className="heading row">
      {artist.images && artist.images.length > 0 ? (
        <Suspense
          maxDuration={300}
          fallback={
            <img
              className="artwork"
              src={artist.images[0].url}
              alt={artist.name}
            />
          }
        >
          <Img
            className="artwork"
            src={artist.images[2].url}
            alt={artist.name}
          />
        </Suspense>
      ) : (
        <IconPerson />
      )}

      <div>
        <h1>{artist.name}</h1>
        <div className="meta">
          {artist.followers.total.toLocaleString()} followers
        </div>
      </div>
    </div>
  );
}

export default ArtistDetails;
