import React from 'react';
import ListItem from './ListItem';
import { fetchArtistRelatedArtistsJSON } from '../api';
import { createResource } from 'react-cache';

const ArtistRelatedArtistsResource = createResource(
  fetchArtistRelatedArtistsJSON
);
class ArtistRelatedArtists extends React.Component {
  render() {
    const artists = ArtistRelatedArtistsResource.read(this.props.id);
    return (
      <div>
        <h3>Related Artists</h3>
        {artists.slice(0, 3).map(item => (
          <ListItem to={`/artist/${item.id}`} key={item.id} item={item} />
        ))}
      </div>
    );
  }
}

export default ArtistRelatedArtists;
